def check_function_properties(f_expr, var, properties):
    """
    Checks specified properties of the function such as even, odd, increasing, or decreasing.
    Parameters:
    f_expr (str or sympy expression): The function expression.
    var (str): The variable in the function.
    properties (list): List of properties to check, e.g., ['even', 'odd', 'increasing']
    Returns:
    Dict: Dictionary stating the result of each property check.
    Example:
    --------
    >>> check_function_properties('x**2', 'x', ['even', 'increasing'])
    {'even': True, 'increasing': False}
    """
    x = sp.symbols(var)
    func = sp.sympify(f_expr)
    results = {}
    for prop in properties:
        if prop == 'even':
            results['even'] = sp.simplify(func.subs(x, -x) - func) == 0
        elif prop == 'odd':
            results['odd'] = sp.simplify(func.subs(x, -x) + func) == 0
        elif prop == 'increasing':
            derivative = sp.diff(func, x)
            results['increasing'] = sp.solve(derivative > 0)
        elif prop == 'decreasing':
            derivative = sp.diff(func, x)
            results['decreasing'] = sp.solve(derivative < 0)
    return results

def evaluate_function(f, var, at_points):
    """
    Evaluate a function at specific points.

    Parameters:
    - f (sympy expression): The function to be evaluated.
    - var (sympy.Symbol): The variable in the expression.
    - at_points (list or single value): Points at which to evaluate the function.

    Returns:
    - dict: Values of the function at specified points.

    Examples:
    >>> x = sp.symbols('x')
    >>> f = sp.piecewise_fold(sp.Piecewise((x**2, x<0), (sp.sin(x), x>=0)))
    >>> evaluate_function(f, x, [-1, 0, 1, 2])
    {-1: 1, 0: 0, 1: sin(1), 2: sin(2)}
    """
    if isinstance(at_points, (list, tuple)):
        return {p: f.subs(var, p) for p in at_points}
    else:
        return {at_points: f.subs(var, at_points)}

def find_horizontal_asymptotes(expr, var):
    """
    Identify and calculate the horizontal asymptotes of a rational function.

    Parameters:
    - expr (str or sp.Expr): The rational expression for which to find horizontal asymptotes, provided as a string or sympy expression.
    - var (str or sp.Symbol): The variable in the expression, provided as a string or sympy symbol.

    Returns:
    - asymptotes (list): A list containing the y-values of horizontal asymptotes. The list may be empty if no horizontal asymptotes exist.
    Example:
    >>> find_horizontal_asymptotes("x**2 / (x+1)", "x")
    [1]
    """
    if isinstance(expr, str):
        expr = sp.sympify(expr)
    if isinstance(var, str):
        var = sp.Symbol(var)
    asymptotes = []
    # Check limits at positive and negative infinity
    for point in [sp.oo, -sp.oo]:
        try:
            lim_val = sp.limit(expr, var, point)
            if lim_val.is_real:
                asymptotes.append(lim_val)
        except (sp.PoleError, ValueError):
            continue
    return list(set(asymptotes))  # Return unique asymptotes

