def reflect_point(point, axis='y'):
    """
    Reflect a point across the y-axis or x-axis.

    Parameters:
        point (tuple): The point to reflect, given as (x, y).
        axis (str): The axis to reflect across ('x' or 'y').

    Returns:
        tuple: The reflected point.
    """
    if axis == 'y':
        return (-point[0], point[1])
    elif axis == 'x':
        return (point[0], -point[1])
    else:
        raise ValueError("Axis must be 'x' or 'y'")

def reflect_across_axis(point, axis='y'):
    """
    Reflect a point across a specified axis in the Cartesian coordinate system.
    Parameters:
    ----------
    point : tuple
        A tuple (x, y) representing the initial coordinates of the point.
    axis : str
        The axis to reflect across ('x', 'y', 'y=x', 'y=-x').
    Returns:
    -------
    tuple
        A tuple representing the reflected point's coordinates.
    Example:
    -------
    >>> reflect_across_axis((2, 3), 'y')
    (-2, 3)
    >>> reflect_across_axis((2, 3), 'y=x')
    (3, 2)
    """
    x, y = point
    if axis == 'y':
        return (-x, y)
    elif axis == 'x':
        return (x, -y)
    elif axis == 'y=x':
        return (y, x)
    elif axis == 'y=-x':
        return (-y, -x)
    else:
        raise ValueError("Invalid axis for reflection.")

def reflect(point, axis="x"):
    """
    Reflect a point across a specified axis in 2D.

    Parameters:
        point (tuple): Coordinates (x, y) of the point.
        axis (str): Axis you want to reflect over, "x", "y", or "origin". Additionally supports "y=x".

    Returns:
        tuple: Coordinates of the reflected point.

    Examples:
        reflect((2, 3), axis="x")
        reflect((2, 3), axis="y")
        reflect((2, 3), axis="origin")
        reflect((2, 3), axis="y=x")
    """
    x, y = point
    if axis == "x":
        return (x, -y)
    elif axis == "y":
        return (-x, y)
    elif axis == "origin":
        return (-x, -y)
    elif axis == "y=x":
        return (y, x)
    else:
        raise ValueError("Invalid axis specified.")

def line_equation(x1, y1, x2, y2, return_symbolic=True):
    """
    Generate the equation of the line passing through two points (x1, y1) and (x2, y2).

    Parameters:
        x1, y1 (float): Coordinates of the first point.
        x2, y2 (float): Coordinates of the second point.
        return_symbolic (bool): If True, returns a symbolic equation, otherwise returns slope and intercept.

    Returns:
        sympy.Eq or tuple: Depending on return_symbolic flag, returns the symbolic equation or a tuple (slope, intercept).
    """
    m = slope(x1, y1, x2, y2)
    if m is None:  # Vertical line
        if return_symbolic:
            return sp.Eq(sp.Symbol('x'), x1)
        else:
            return None, x1  # No slope, x-intercept is x1

    # Equation of the line: y = mx + c; solve for c
    c = y1 - m * x1
    if return_symbolic:
        x, y = sp.symbols('x y')
        return sp.Eq(y, m * x + c)
    else:
        return m, c

