def rectangle_properties_from_coordinates(points):
    """
    Determine if the given four points can form a rectangle and calculate its area and perimeter.
    Parameters:
    points (ndarray): Nx2 ndarray where N is number of points (should be 4) representing the coordinates of the vertices of the quadrilateral.
    Returns:
    dict: A dictionary with keys 'is_rectangle', 'area', and 'perimeter' or 'error' if inputs are not valid.

    Example:
    >>> rectangle_properties_from_coordinatess(np.array([[0,0], [4,0], [4,3], [0,3]]))
    {'is_rectangle': True, 'area': 12, 'perimeter': 14}
    """
    if points.shape != (4, 2):
        return {'error': 'Invalid input'}

    # Calculating distances
    def distance(p1, p2):
        return np.sqrt((p2[0] - p1[0])**2 + (p2[1] - p1[1])**2)
    sides = [
        distance(points[0], points[1]),
        distance(points[1], points[2]),
        distance(points[2], points[3]),
        distance(points[3], points[0])
    ]
    diagonals = [
        distance(points[0], points[2]),
        distance(points[1], points[3])
    ]
    # Rectangle properties: Opposite sides are equal and diagonals are equal
    if np.isclose(sides[0], sides[2]) and np.isclose(sides[1], sides[3]) and np.isclose(diagonals[0], diagonals[1]):
        area = sides[0] * sides[1]
        perimeter = 2 * (sides[0] + sides[1])
        return {'is_rectangle': True, 'area': area, 'perimeter': perimeter}
    else:
        return {'is_rectangle': False}

