def calculate_area(shape, dimensions):
    """
    Given the shape and its dimensions, calculate its area.

    Parameters
    ----------
    shape : str
        The type of the shape. Can be 'circle', 'rectangle', 'triangle', 'trapezoid' or 'square'.
    dimensions : tuple of float
        The dimensions of the shape. For 'circle', it is the radius; for 'rectangle', it is (length, width); for 'triangle', it is (base, height); for 'trapezoid', it is (base1, base2, height); for 'square', it is the side length.
    Returns
    -------
    float
        The area of the shape.
    Raises
    ------
    ValueError
        If the shape is not recognized, or if incorrect dimensions are given.
    """
    if shape == 'circle':
        if len(dimensions) != 1:
            raise ValueError("A circle requires one dimension (radius).")
        return math.pi * (dimensions[0]**2)
    elif shape == 'rectangle':
        if len(dimensions) != 2:
            raise ValueError("A rectangle requires two dimensions (length, width).")
        return dimensions[0] * dimensions[1]
    elif shape == 'triangle':
        if len(dimensions) != 2:
            raise ValueError("A triangle requires two dimensions (base, height).")
        return 0.5 * dimensions[0] * dimensions[1]
    elif shape == 'trapezoid':
        if len(dimensions) != 3:
            raise ValueError("A trapezoid requires three dimensions (base1, base2, height).")
        return 0.5 * (dimensions[0] + dimensions[1]) * dimensions[2]
    elif shape == 'square':
        if len(dimensions) != 1:
            raise ValueError("A square requires one dimension (side length).")
        return dimensions[0] ** 2
    else:
        raise ValueError("Shape not recognized.")

