def triangle_side_lengths(angle, *dimensions):
    """
    Calculate the length of sides in common triangle types (right, 30-60-90, 45-45-90).
    Parameters
    ----------
    angle : int
        Internal angle in degrees of the triangle.
    dimensions : tuple
        Lengths of known sides.
    Returns
    -------
    tuple
        Lengths of all sides of the triangle.
    Examples
    --------
    >>> triangle_side_lengths(90, 3, 4)
    (3, 4, 5)
    >>> triangle_side_lengths(30, 5)
    (5, 5*sqrt(3), 10)
    >>> triangle_side_lengths(45, 5)
    (5, 5, 5*sqrt(2))
    """

    import sympy as sp
    def right_triangle_sides(a, b):
        # Use the Pythagorean theorem
        return a, b, sp.sqrt(a**2 + b**2)
    def special_triangle_sides(angle, a):
        if angle == 30:
            return a, a * sp.sqrt(3), 2*a
        elif angle == 45:
            return a, a, a * sp.sqrt(2)
        else:
            raise ValueError('This tool only covers 30-60-90 and 45-45-90 triangles.')
    if angle == 90:
        return right_triangle_sides(*dimensions)
    else:
        return special_triangle_sides(angle, *dimensions)

