def calculate_side_from_angle(isosceles_base_angle, side_length="base"):
    """
    Calculate the side lengths in an isosceles triangle from a known base angle.

    Parameters:
    isosceles_base_angle (float): The base angle of the isosceles triangle in degrees.
    side_length (str): Specifies the side to calculate 'base' or 'leg'.

    Returns:
    float: The length of the specified side.
    Example:
    >>> calculate_side_from_angle(45, "leg")
    1.414  # Assuming the other side length (base) is 1
    """
    # Calculate the leg if the base is 1, using trigonometry, tan(theta) = opposite/adjacent
    if side_length == "leg":
        return math.tan(math.radians(isosceles_base_angle))
    elif side_length == "base":
        return 2 * math.tan(math.radians(isosceles_base_angle) / 2)
    else:
        raise ValueError("Invalid side_length specified, choose either 'base' or 'leg'")

def calculate_special_triangle_sides(triangle_type, known_side, known_side_type):
    """
    Calculate the sides of a 30-60-90 or 45-45-90 triangle given one side.

    Parameters:
        triangle_type (str): Type of the triangle. Either '30-60-90' or '45-45-90'.
        known_side (float): The length of the known side.
        known_side_type (str): The type of the known side: 'shorter leg', 'longer leg', or 'hypotenuse'.

    Returns:
        tuple: All three sides (short_leg, long_leg, hypotenuse) of the triangle.
    Example:
        >>> calculate_special_triangle_sides('30-60-90', 1, 'shorter leg')
        (1, sqrt(3), 2)
        >>> calculate_special_triangle_sides('45-45-90', sqrt(2), 'hypotenuse')
        (1, 1, sqrt(2))
    """
    if triangle_type == '30-60-90':
        if known_side_type == 'shorter leg':
            short_leg = known_side
            long_leg = short_leg * math.sqrt(3)
            hypotenuse = short_leg * 2
        elif known_side_type == 'longer leg':
            long_leg = known_side
            short_leg = long_leg / math.sqrt(3)
            hypotenuse = short_leg * 2
        elif known_side_type == 'hypotenuse':
            hypotenuse = known_side
            short_leg = hypotenuse / 2
            long_leg = short_leg * math.sqrt(3)
    elif triangle_type == '45-45-90':
        if known_side_type == 'leg':
            leg = known_side
            hypotenuse = leg * math.sqrt(2)
            return (leg, leg, hypotenuse)
        elif known_side_type == 'hypotenuse':
            hypotenuse = known_side
            leg = hypotenuse / math.sqrt(2)
            return (leg, leg, hypotenuse)
    else:
        raise ValueError("Unknown triangle type or side type")

    return (short_leg, long_leg, hypotenuse)

def compute_special_triangle_sides(triangle_type, known_side, known_side_type):
    """
    Compute the side lengths of a 30-60-90 or a 45-45-90 triangle given one length.
    Parameters
    ----------
    triangle_type : str
        Type of the triangle. It can be either "30-60-90" or "45-45-90".
    known_side : float
        Known length of the side.
    known_side_type : str
        Type of the known side. It can be "short leg", "long leg", or "hypotenuse".

    Returns
    -------
    dict
        A python dict includes all sides' lengths.
    Example
    -------
    >>> compute_special_triangle_sides("30-60-90", 5, "short leg")
    {'short leg': 5, 'long leg': 5*sqrt(3), 'hypotenuse': 10}
    """
    if triangle_type == "30-60-90":
        if known_side_type == "short leg":
            return {'short leg': known_side, 'long leg': known_side*math.sqrt(3), 'hypotenuse': 2*known_side}
        elif known_side_type == "long leg":
            return {'short leg': known_side/math.sqrt(3), 'long leg': known_side, 'hypotenuse': 2*(known_side/math.sqrt(3))}
        elif known_side_type == "hypotenuse":
            return {'short leg': known_side/2, 'long leg': (known_side/2)*math.sqrt(3), 'hypotenuse': known_side}
    elif triangle_type == "45-45-90":
        return {'leg1': known_side/math.sqrt(2), 'leg2': known_side/math.sqrt(2), 'hypotenuse': known_side}

def pythagorean_theorem(a=None, b=None, c=None):
    """
    Apply the Pythagorean theorem to find the missing side of a right triangle.
    Parameters:
        a (float, optional): length of side a, leg of the triangle
        b (float, optional): length of side b, leg of the triangle
        c (float, optional): length of side c, hypotenuse of the triangle
    Returns:
        float: The length of the missing side.
    Examples:
    >>> pythagorean_theorem(a=3, b=4)
    5.0
    >>> pythagorean_theorem(c=10, b=6)
    8.0
    """
    import math

    if a is None:
        return math.sqrt(c**2 - b**2)
    elif b is None:
        return math.sqrt(c**2 - a**2)
    elif c is None:
        return math.sqrt(a**2 + b**2)
    else:
        raise ValueError("Exactly one side must be None to find its length")

def calculate_right_triangle_sides(known_side, hypotenuse=None, other_side=None):
    """
    Given a side and either the hypotenuse or the other side, calculate the missing side using Pythagoras' theorem.
    Parameters:
    - known_side (float): Length of one known side of the right triangle.
    - hypotenuse (float, optional): Hypotenuse of the triangle, if known.
    - other_side (float, optional): The other side (leg) of the triangle, if known.
    Returns:
    float: The length of the missing side.

    Example:
    >>> calculate_right_triangle_sides(3, hypotenuse=5)
    4.0
    """
    if hypotenuse:
        return math.sqrt(hypotenuse**2 - known_side**2)
    elif other_side:
        return math.sqrt(known_side**2 + other_side**2)
    else:
        raise ValueError('Either hypotenuse or the other side must be known.')

