def calculate_right_triangle_side(a=None, b=None, c=None):
    """
    Calculate the missing side of a right triangle using the Pythagorean Theorem.

    Parameters:
        a (float): The length of side a, or None if it is the unknown side.
        b (float): The length of side b, or None if it is the unknown side.
        c (float): The length of the hypotenuse c, or None if it is the unknown side.

    Returns:
        float: The length of the missing side.

    Examples:
        calculate_right_triangle_side(a=3, b=4)  # Should return 5 as the hypotenuse
        calculate_right_triangle_side(a=3, c=5)  # Should return 4 as the other side
        calculate_right_triangle_side(b=4, c=5)  # Should return 3 as the other side
    """
    import math
    if c is None:
        # Calculate hypotenuse
        return math.sqrt(a ** 2 + b ** 2)
    elif a is None:
        # Calculate missing side a
        return math.sqrt(c ** 2 - b ** 2)
    elif b is None:
        # Calculate missing side b
        return math.sqrt(c ** 2 - a ** 2)
    else:
        raise ValueError("One and only one side length must be None.")

def pythagorean_solver(a=None, b=None, c=None):
    """
    Calculates the missing side of a right triangle or checks if the given sides form a right triangle.

    Parameters:
        a (float, optional): The length of side a.
        b (float, optional): The length of side b.
        c (float, optional): The length of the hypotenuse c.

    Returns:
        float or dict: The size of the missing side if one side is provided as None. If all sides are provided,
                       returns a dictionary with details whether it's a right triangle and by what margin it deviates.

    Examples:
        >>> pythagorean_solver(a=3, b=4)
        5.0
        >>> pythagorean_solver(c=5, b=4)
        3.0
        >>> pythagorean_solver(a=3, b=4, c=5)
        {'is_right_triangle': True, 'deviation': 0}
    """
    # Calculate missing side if one side is None
    if c is None and a is not None and b is not none:
        c = sqrt(a**2 + b**2)
        return c.evalf()
    elif a is None and b is not None and c is not None:
        a = sqrt(c**2 - b**2)
        return a.evalf()
    elif b is None and a is not None and c is not None:
        b = sqrt(c**2 - a**2)
        return b.evalf()
    # Check if the given sides form a right triangle
    if a is not None and b is not None and c is not None:
        expected_c2 = a**2 + b**2
        deviation = abs(c**2 - expected_c2)
        return {'is_right_triangle': math.isclose(c**2, expected_c2), 'deviation': deviation}

def pythagorean_theorem(known_side1, known_side2, find='hypotenuse'):
    """
    Solves for a side of a right triangle using the Pythagorean theorem.

    Parameters:
        known_side1 (float): Length of first known side.
        known_side2 (float): Length of second known side.
        find (str, optional): Which side to find; choices are 'hypotenuse' or 'leg'. Default is 'hypotenuse'.

    Returns:
        float: Length of the unknown side.

    Examples:
        >>> pythagorean_theorem(3, 4)
        5.0
        >>> pythagorean_theorem(5, 4, find='leg')
        3.0
    """
    if find == 'hypotenuse':
        return math.sqrt(known_side1**2 + known_side2**2)
    elif find == 'leg':
        if known_side1 > known_side2:
            return math.sqrt(known_side1**2 - known_side2**2)
        else:
            return math.sqrt(known_side2**2 - known_side1**2)
    else:
        raise ValueError("Invalid option for 'find'. Choose 'hypotenuse' or 'leg'.")

def find_missing_side(known_sides):
    """
    Finds the missing side of a right triangle given the other two sides.

    Parameters:
    known_sides (list of float or None): List of two known sides and one None indicating the missing side.

    Returns:
    float: The missing side of the right triangle.

    Examples:
    >>> find_missing_side([3, 4, None])
    5.0
    >>> find_missing_side([None, 4, 5])
    3.0
    """
    a, b, c = known_sides
    if a is None:
        return math.sqrt(c**2 - b**2)
    elif b is None:
        return math.sqrt(c**2 - a**2)
    else:  # c is None
        return math.sqrt(a**2 + b**2)

