def polyhedron_properties(shape_type, base_edges=0):
    """
    Calculate properties such as vertices, edges, and faces for common polyhedra.
    Parameters
    ----------
    shape_type : str
        Type of the polyhedron: 'triangular_prism', 'rectangular_prism', 'pyramid'
    base_edges : int, optional
        The number of edges in the pyramid's base if the shape is a 'pyramid'. Default is 0.
    Returns
    -------
    dict
        Dictionary containing 'vertices', 'edges', and 'faces' keys with integer values.
    Examples
    --------
    >>> polyhedron_properties('triangular_prism')
    {'vertices': 6, 'edges': 9, 'faces': 5}
    >>> polyhedron_properties('pyramid', base_edges=4)
    {'vertices': 5, 'edges': 8, 'faces': 5}
    """
    if shape_type == 'triangular_prism':
        return {'vertices': 6, 'edges': 9, 'faces': 5}
    elif shape_type == 'rectangular_prism':
        return {'vertices': 8, 'edges': 12, 'faces': 6}
    elif shape_type == 'pyramid':
        if base_edges <= 0:
            raise ValueError("Base edges must be greater than zero for pyramids")
        return {'vertices': base_edges + 1, 'edges': 2 * base_edges, 'faces': base_edges + 1}
    else:
        raise ValueError("Unknown shape type specified")

def count_unit_cubes_with_painted_faces(n, faces_painted):
    """
    Calculate the number of unit cubes with a certain number of painted faces in an n x n x n cube.

    Parameters:
    n (int): The side length of the cube in terms of number of unit cubes.
    faces_painted (int): The number of faces that are painted on the unit cubes.

    Returns:
    int: The count of unit cubes with exactly the specified number of painted faces.

    Example:
    >>> count_unit_cubes_with_painted_faces(3, 1)
    6
    """
    if faces_painted == 1:
        return 6 * (n - 2)
    elif faces_painted == 2:
        return 12 * (n - 2)
    elif faces_painted == 3:
        return 8
    return 0

