def reflect_point(point, line):
    """
    Reflects a point across a specified line in 2D space.
    Parameters:
    - point (tuple of float): The coordinates of the point to reflect (x, y).
    - line (str): the line across which to reflect ('y-axis', 'x-axis', 'y=x', 'y=-x').
    Returns:
    - tuple: The coordinates of the reflected point.

    Examples:
    >>> reflect_point((2, 3), 'y-axis')
    (-2, 3)
    >>> reflect_point((2, 3), 'x-axis')
    (2, -3)
    >>> reflect_point((3, 2), 'y=x')
    (2, 3)
    >>> reflect_point((3, 2), 'y=-x')
    (-2, -3)
    """
    if line == 'y-axis':
        return (-point[0], point[1])
    elif line == 'x-axis':
        return (point[0], -point[1])
