def get_conic_section_type(equation):
    """
    Identifies the type of conic section based on the coefficients of the general second degree equation Ax^2 + Bxy + Cy^2 + Dx + Ey + F = 0.
    Parameters:
    equation : sympy.Expr
        The equation representing the conic section in sympy expression format.
    Returns:
    str: The type of the conic section: 'circle', 'ellipse', 'parabola', 'hyperbola', or 'unknown'.
    """
    # Assuming the equation is given in a format sympy can understand (Ax^2 + Bxy + Cy^2 + Dx + Ey + F = 0)
    A, B, C = equation.as_coefficients_dict()[x**2], equation.as_coefficients_dict()[x*y], equation.as_coefficients_dict()[y**2]

    # Conditions for identifying the conic section
    if B**2 - 4*A*C < 0:
        if A == C:
            return 'circle'
        else:
            return 'ellipse'
    elif B**2 - 4*A*C == 0:
        return 'parabola'
    elif B**2 - 4*A*C > 0:
        return 'hyperbola'
    else:
        return 'unknown'

def get_conic_section_characteristics(equation):
    """
    Calculates and returns the characteristics of the conic section based on its equation.
    Parameters:
    equation : sympy.Expr
        The equation representing the conic section in sympy expression format.
    Returns:
    dict: A dictionary containing the characteristics of the conic section such as type, center, axes lengths, foci, vertices, and orientation.
    """
    conic_type = get_conic_section_type(equation)
    characteristics = {'type': conic_type}

    if conic_type == 'circle' or conic_type == 'ellipse':
        conic = ellipse.Ellipse(equation=equation)
    elif conic_type == 'hyperbola':
        conic = hyperbola.Hyperbola(equation=equation)
    elif conic_type == 'parabola':
        conic = parabola.Parabola(directrix=Line(equation), focus=Point(0, 0))
    else:
        return characteristics

    characteristics.update({
        'center': conic.center,
        'foci': conic.foci,
        'vertices': conic.vertices,
        'axis_lengths': (conic.hradius, conic.vradius) if conic_type != 'parabola' else (None, None) 
    })

    if conic_type == 'hyperbola':
        characteristics['asymptotes'] = conic.asymptotes

    return characteristics

