def pythagorean_theorem(a=None, b=None, c=None):
    """
    Given any two sides of a right triangle, calculate the third side using the Pythagorean Theorem.
    Parameters:
    - a (float, optional): one leg of the right triangle.
    - b (float, optional): the other leg of the right triangle.
    - c (float, optional): the hypotenuse of the right triangle.

    Returns:
    - float: the length of the missing side.
    Examples:
    >>> pythagorean_theorem(a=3, b=4)
    5.0
    >>> pythagorean_theorem(c=5, b=4)
    3.0
    """
    if a is None:
        return math.sqrt(c**2 - b**2)
    elif b is None:
        return math.sqrt(c**2 - a**2)
    elif c is None:
        return math.sqrt(a**2 + b**2)
    else:
        return None
