def differentiate_function(expr, var, point=None):

    try:
        derivative = diff(expr, var)
        if point is not None:
            return derivative.subs(var, point)
        return derivative
    except:
        return "Can't calculate derivative"

def evaluate_function_at_point(function_expression, point):

    x = symbols('x')
    expression = sympify(function_expression)
    func = lambdify(x, expression, 'numpy')
    return func(point)

def reflect_function(expression, axis='x'):

    x = symbols('x')
    func = sympify(expression)
    if axis == 'x':
        reflected_func = func * -1
    elif axis == 'y':
        reflected_func = func.subs(x, -x)
    else:
        raise ValueError("axis must be 'x' or 'y'")
    return reflected_func

def find_function_range(f, variable):

    derivative = diff(f, variable)
    critical_points = solve(derivative, variable)

    if not critical_points:
        return (-oo, oo)

    critical_values = [f.subs(variable, cp) for cp in critical_points] + [f.subs(variable, oo), f.subs(variable, -oo)]
    return (min(critical_values), max(critical_values))

def find_intersection(f1, f2, var):
    if isinstance(f1, (int, float)) and isinstance(f2, (int, float)):
        raise ValueError("Both f1 and f2 cannot be constant values.")

    if isinstance(f1, (int, float)):
        f1 = sp.Number(f1)
    elif isinstance(f2, (int, float)):
        f2 = sp.Number(f2)

    if isinstance(f1, sp.Expr) and isinstance(f2, sp.Expr):
        return sp.solve((f1, f2), var)
    else:
        return sp.solve(sp.Eq(f1, f2), var)

def find_function_range(f, variable):

    x = sp.symbols(variable)
    derivative = sp.diff(f, x)
    critical_points = sp.solve(derivative, x)
    critical_values = [f.subs(x, cp) for cp in critical_points] + [f.subs(x, sp.oo), f.subs(x, -sp.oo)]
    return (min(critical_values), max(critical_values))
