def data_to_percentage(data):

    total = sum(data)
    if total == 0:
        return [0]*len(data)
    return [(100.0 * x / total) for x in data]

def calculate_percentages(values, round_digits=2):

    import numpy as np
    total = np.sum(values)
    if total == 0:
        return [0] * len(values)
    percentages = [(x / total * 100) for x in values]
    return [round(pct, round_digits) for pct in percentages]

