def analyze_bar_graph_data(data):

    max_label = max(data, key=data.get)
    min_label = min(data, key=data.get)
    response = {
        'max_value': data[max_label],
        'max_label': max_label,
        'min_value': data[min_label],
        'min_label': min_label,
        'total_sum': sum(data.values())
    }
    # Calculating differences
    labels = list(data.keys())
    response['differences'] = {}
    for i, label1 in enumerate(labels):
        for label2 in labels[i+1:]:
            diff_key = f'{label2}-{label1}'
            response['differences'][diff_key] = data[label2] - data[label1]

    return response

def plot_data_and_percentage(labels, values, chart_type='bar', figsize=(10, 7), title="Data Distribution"):

    plt.figure(figsize=figsize)
    total = sum(values)
    percentages = [100 * v / total for v in values]
    if chart_type.lower() == 'bar':
        plt.bar(labels, values, color='skyblue')
        plt.ylabel('Values')
        for i, (value, percentage) in enumerate(zip(values, percentages)):
            plt.text(i, value, '{:.1f}%'.format(percentage), ha='center', va='bottom')
    elif chart_type.lower() == 'pie':
        plt.pie(values, labels=labels, autopct='%1.1f%%', startangle=90, colors=plt.cm.Paired.colors)
    else:
        raise ValueError("chart_type must be either 'bar' or 'pie'")
    plt.title(title)
    plt.show()

def calculate_mean(data):

    values = []
    for num, freq in data.items():
        values.extend([num] * freq)
    mean = sum(values) / len(values)
    return mean
