def line_equation_from_point_slope(point: tuple, slope: float) -> tuple:

    if not isinstance(point, tuple) or len(point) != 2 or not all(isinstance(coord, (int, float)) for coord in point):
        raise TypeError("The point must be a tuple of two numbers.")

    x, y = point

    if not isinstance(slope, (int, float)):
        raise TypeError("The slope must be a number.")

    if slope == float('inf'):
        raise ValueError("The slope cannot be infinite (vertical line).")

    if slope == 0:
        raise ValueError("The slope cannot be zero (horizontal line).")

    if math.isnan(slope):
        raise ValueError("The slope cannot be NaN.")

    c = y - slope * x
    return slope, c

