def calculate_perimeter_and_area(shape_type, dimensions):

    import math

    def rectangle(length, width):
        perimeter = 2 * (length + width)
        area = length * width
        return perimeter, area
    def circle(radius):
        perimeter = 2 * math.pi * radius
        area = math.pi * radius ** 2
        return perimeter, area
    def square(side_length):
        perimeter = 4 * side_length
        area = side_length ** 2
        return perimeter, area
    def trapezoid(base1, base2, height):
        perimeter = base1 + base2 + 2 * math.sqrt(height**2 + ((base2 - base1) / 2) ** 2)
        area = 0.5 * (base1 + base2) * height
        return perimeter, area
    def complex_shape(squares, circles):
        total_perimeter = 0
        total_area = 0

        for side in squares:
            total_perimeter += 4 * side
            total_area += side ** 2

        for radius in circles:
            total_perimeter += 2 * math.pi * radius
            total_area += math.pi * radius ** 2

        return total_perimeter, total_area
    if shape_type == 'rectangle':
        return rectangle(dimensions['length'], dimensions['width'])
    elif shape_type == 'circle':
        return circle(dimensions['radius'])
    elif shape_type == 'square':
        return square(dimensions['side_length'])
    elif shape_type == 'trapezoid':
        return trapezoid(dimensions['base1'], dimensions['base2'], dimensions['height'])
    elif shape_type == 'complex':
        return complex_shape(dimensions['squares'], dimensions['circles'])
    else:
        raise ValueError("Unsupported shape type.")

