def TriangleAreaCalculator(base=None, height=None, vertices=None, sides=None):

    import math
    if base is not None and height is not None:
        # Simple calculation from base and height
        return 0.5 * base * height
    elif vertices is not None:
        # Calculate area from vertices using determinant (shoelace formula)
        x1, y1 = vertices[0]
        x2, y2 = vertices[1]
        x3, y3 = vertices[2]
        return 0.5 * abs(x1*(y2-y3) + x2*(y3-y1) + x3*(y1-y2))
    elif sides is not None:
        # Using Heron's formula to find area when sides are known
        a, b, c = sides
        s = 0.5 * sum(sides)
        area = math.sqrt(s * (s - a) * (s - b) * (s - c))
        return area
    else:
        raise ValueError("Insufficient information to calculate area.")

