    if transformation_type.lower() == 'reflection':
        axis = parameters.get('axis', 'x').lower()
        position = parameters.get('position', 0)
        if axis == 'x':
            return np.array([[1, 0], [0, -1]])
        elif axis == 'y':
            return np.array([[-1, 0], [0, 1]])
        else:
            raise ValueError("Invalid axis for reflection. Use 'x' or 'y'.")

    elif transformation_type.lower() == 'dilation':
        center = parameters.get('center', (0, 0))
        scale_factor = parameters.get('scale_factor', 1)
        cx, cy = center
        return np.array([[scale_factor, 0, cx * (1 - scale_factor)], 
                         [0, scale_factor, cy * (1 - scale_factor)], 
                         [0, 0, 1]])

    else:
        raise ValueError("Transformation type not recognized. Use 'reflection' or 'dilation'.")

