def apply_transformations(point, transformations):


    import numpy as np

    def translate(point, vector):
        x, y = point
        dx, dy = vector
        return (x + dx, y + dy)
    def rotate(point, angle, origin=(0, 0)):
        angle_rad = np.radians(angle)
        sin_a, cos_a = np.sin(angle_rad), np.cos(angle_rad)
        x, y = point
        ox, oy = origin
        # Translate point to origin
        x -= ox
        y -= oy
        # Rotate point
        x_new = x * cos_a - y * sin_a
        y_new = x * sin_a + y * cos_a
        # Translate point back
        x_new += ox
        y_new += oy
        return (x_new, y_new)
    def reflect(point, axis, position=0):
        x, y = point
        if axis.lower() == 'y':
            return (2*position - x, y)
        elif axis.lower() == 'x':
            return (x, 2*position - y)
        else:
            raise ValueError("Axis must be 'x' or 'y'.")
    # Apply each transformation in order
    for transformation in transformations:
        t_type = transformation['type']
        if t_type == 'translate':
            point = translate(point, transformation['vector'])
        elif t_type == 'rotate':
            point = rotate(point, transformation['angle'], transformation.get('origin', (0, 0)))
        elif t_type == 'reflect':
            point = reflect(point, transformation['axis'], transformation.get('position', 0))
        else:
            raise ValueError(f"Unsupported transformation type: {t_type}")
    return point

