def reflect_light_path(start_point, plane_point, plane_normal, cube_vertices):

    import numpy as np
    def reflect_point_across_plane(point, plane_normal, plane_point):
        p = np.array(point)
        n = np.array(plane_normal)
        m = np.array(plane_point)
        pm = p - m
        n_norm = n / np.linalg.norm(n)
        dist = np.dot(pm, n_norm)
        reflected_point = p - 2 * dist * n_norm
        return tuple(reflected_point)
    def calculate_distance(point1, point2):
        return np.linalg.norm(np.array(point1) - np.array(point2))
    # Reflect the starting point across the given plane
    reflected_point = reflect_point_across_plane(start_point, plane_normal, plane_point)
    # Calculate the distance to the nearest vertex of the cube
    distances = [calculate_distance(reflected_point, vertex) for vertex in cube_vertices]
    return min(distances)

