def convert_area(area, initial_unit='square_feet', target_unit='square_yards'):
    if initial_unit == 'square_feet' and target_unit == 'square_yards':
        return area / 9
    elif initial_unit == 'square_yards' and target_unit == 'square_feet':
        return area * 9
    elif initial_unit == 'square_feet' and target_unit == 'square_feet':
        return area
    elif initial_unit == 'square_yards' and target_unit == 'square_yards':
        return area
    elif initial_unit == 'inches' and target_unit == 'square_feet':
        return area / 144
    else:
        raise ValueError("Unrecognized unit. Use 'square_feet' or 'square_yards'.")
