    return a / (1 - r)

def generate_sequence(kind, initial_terms, common_diff=None, common_ratio=None, recursive_rule=None, n_terms=10):

    import numpy as np

    if kind == 'arithmetic':
        return [initial_terms[0] + i * common_diff for i in range(n_terms)]
    elif kind == 'geometric':
        return [initial_terms[0] * (common_ratio ** i) for i in range(n_terms)]
    elif kind == 'recursive':
        terms = initial_terms[:]
        while len(terms) < n_terms:
            next_term = recursive_rule(terms)
            terms.append(next_term)
        return terms
    else:
        raise ValueError("Unsupported sequence type")

def solve_for_first_term(target_sum, r, n=None):

    if n is None:  # Infinite series
        if abs(r) < 1:
            return target_sum * (1 - r)
        else:
            return None
    else:  # Finite Series
        return target_sum * (1 - r) / (1 - r**n)

def evaluate_telescoping_series(terms):

    sum_result = 0
    for start, end in terms:
        sum_result += start - end
    return sum_result

def calculate_sequence_values(sequence, n_th=None, sum_indices=None):

    if n_th is not None:
        return sequence[n_th - 1]
    if sum_indices is not None:
        return sum([sequence[i - 1] for i in sum_indices])
    return sequence

