    if position1 == position2:
        raise ValueError("The two positional arguments must be different to compute a common difference.")
    common_difference = (term2 - term1) / (position2 - position1)
    return common_difference

def arithmetic_sequence_properties(a1, an, n=None, d=None):

    if d is not None:
        # Compute n using the formula an = a1 + (n-1)d
        n_value = ((an - a1) / d) + 1
        return {'number_of_terms': int(n_value)}
    elif n is not None:
        # Compute d using the formula d = (an - a1) / (n-1)
        d_value = (an - a1) / (n - 1)
        return {'common_difference': d_value}
    else:
        raise ValueError("Either 'n' or 'd' must be provided.")

def number_of_terms_given_sum(a, d, total_sum):

    import math
    # using derived quadratic formula from the sum formula: n^2 + n(2a - 1)/d - 2S/d = 0
    discriminant = ((2 * a - d) ** 2) + 8 * d * total_sum
    n = (-2 * a + d + math.sqrt(discriminant)) / (2 * d)
    return round(n)

