    return a * (1 - r**n) / (1 - r)

def sum_of_arithmetic_series(n, a, l):

    if n < 1 or not isinstance(n, int):
        raise ValueError("n must be a positive integer")

    return (n * (a + l)) // 2

def geometric_series_sum(first_term, ratio, number_of_terms):

    if number_of_terms <= 0:
        raise ValueError("The number of terms must be positive.")

    return first_term * (1 - ratio ** number_of_terms) / (1 - ratio)

def sum_of_infinite_geometric_series(a, r):

    if abs(r) < 1:
        return a / (1 - r)
    return None

def identify_first_term_and_ratio(terms):

    if len(terms) < 2:
        raise ValueError("At least two terms are required to determine the common ratio.")
    first_term = terms[0]
    common_ratio = terms[1] / terms[0] if terms[0] != 0 else None
    if any(terms[i] * common_ratio != terms[i + 1] for i in range(len(terms) - 1)):
        raise AssertionError("The provided terms do not form a geometric sequence with a constant ratio.")
    return (first_term, common_ratio)

def geometric_series_details(a, r, n):

    nth_term = a * r**(n-1)
    return {
        'first_term': a,
        'common_ratio': r,
