    }

def min_terms_for_sum(a, r, S):

    if r == 1:
        return math.ceil(S / a)
    else:
        n = math.log(1 + (S * (r - 1) / a), r)
        return math.ceil(n)

def find_term_for_geometric_inequality(a, r, threshold, direction='greater'):

    import math

    if direction not in ['greater', 'less']:
        return -1  # Invalid direction input
    if r <= 0 or a == 0:
        return -1  # Invalid ratio or initial term

    if direction == 'greater':
        # Solve a*r^(n-1) > threshold
        # taking logarithm both sides: (n-1) * log(r) > log(threshold / a)
        if r > 1:
            n = math.ceil((math.log(threshold / a) / math.log(r)) + 1)
            return n
        return -1  # No solution exists if r <= 1
    elif direction == 'less':
        # Solve a*r^(n-1) < threshold
        # taking logarithm both sides: (n-1) * log(r) < log(threshold / a)
        if r < 1 and r > 0:
            n = math.ceil((math.log(threshold / a) / math.log(r)) + 1)
            return n
        return -1  # No solution exists if r >= 1
    return -1  # Should not reach here

def geometric_common_ratio(terms):

    if len(terms) < 2:
        raise ValueError("At least two terms are required to determine the common ratio.")
    return terms[1] / terms[0]

def is_geometric_sequence(sequence):

    if len(sequence) < 2:
        return False
    ratios = set(sequence[i] / sequence[i-1] for i in range(1, len(sequence)))
    return len(ratios) == 1

def filter_series_by_term_parity(first_term, common_ratio, num_terms, parity='even'):

    terms = [first_term * (common_ratio ** i) for i in range(num_terms)]
    if parity == 'even':
        return terms[1::2]  # Selecting terms at even indices, zero-based index means odd powers
    else:
        return terms[0::2]  # Selecting terms at odd indices, zero-based index means even powers

