def series_sum(a, r, series_type):

    import numpy as np
    if series_type == 'geometric':
        if np.abs(r) >= 1:
            raise ValueError("The series does not converge because |r| >= 1.")
        return a / (1 - r)
    elif series_type == 'arithmetic':
        if np.abs(r) >= 1:
            raise ValueError("The series does not converge because |r| >= 1.")
        return a / (1 - r)
    else:
        raise ValueError("Invalid series type. Supported types are 'geometric' and 'arithmetic'.")

