def find_term_for_geometric_inequality(a, r, threshold, direction='greater'):

    import math

    if direction not in ['greater', 'less']:
        return -1  # Invalid direction input
    if r <= 0 or a == 0:
        return -1  # Invalid ratio or initial term

    if direction == 'greater':
        # Solve a*r^(n-1) > threshold
        # taking logarithm both sides: (n-1) * log(r) > log(threshold / a)
        if r > 1:
            n = math.ceil((math.log(threshold / a) / math.log(r)) + 1)
            return n
        return -1  # No solution exists if r <= 1
    elif direction == 'less':
        # Solve a*r^(n-1) < threshold
        # taking logarithm both sides: (n-1) * log(r) < log(threshold / a)
        if r < 1 and r > 0:
            n = math.ceil((math.log(threshold / a) / math.log(r)) + 1)
            return n
        return -1  # No solution exists if r >= 1
    return -1  # Should not reach here

