def calculate_infinite_geometric_common_ratio(terms):

    if len(terms) < 2:
        raise ValueError("At least two terms are required to determine the common ratio.")

    # Calculate the common ratio by dividing a term by its preceding term
    common_ratio = terms[-1] / terms[-2]

    return common_ratio

def find_number_of_terms_in_infinite_geometric_sequence(first_term, common_ratio, sum_of_terms):

    import math

    if common_ratio == 1:
        return math.inf  # Infinite terms in the sequence if the common ratio is 1

    n = math.log(sum_of_terms / first_term, common_ratio) + 1

    return n
