def set_operations(set_list, operation="union"):

    import functools
    result_set = set_list[0]  # Initialize with the first set in the list.
    # Apply the appropriate set operation.
    if operation == "union":
        result_set = functools.reduce(lambda a, b: a.union(b), set_list[1:], result_set)
    elif operation == "intersection":
        result_set = functools.reduce(lambda a, b: a.intersection(b), set_list[1:], result_set)
    elif operation == "difference":
        result_set = functools.reduce(lambda a, b: a.difference(b), set_list[1:], result_set)
    elif operation == "symmetric_difference":
        result_set = functools.reduce(lambda a, b: a.symmetric_difference(b), set_list[1:], result_set)

    return result_set

def union_intersection_sizes(*sets):

    from functools import reduce
    if not sets:
        return (0, 0)

    # Set up the first set for intersection starting comparison
    set_union = sets[0].copy()
    set_intersection = sets[0].copy()

    # Apply union and intersection across all sets provided
    for s in sets[1:]:
        set_union.update(s)
        set_intersection.intersection_update(s)
    return (len(set_union), len(set_intersection))
