def calculate_mean(data=None, total_sum=None, count=None):

    if data is not None:
        if not data:  # Edge case check if empty list
            raise ValueError("Empty data list provided.")
        total_sum = sum(data)
        count = len(data)
    elif total_sum is None or count is None:
        raise ValueError("Insufficient parameters provided. Need either `data` or both `total_sum` and `count`.")

    if count == 0:
        raise ValueError("Count of elements is zero, division by zero encountered.")

    mean = total_sum / count
    return mean

def calculate_mean_rounded(data, precision=0):

    mean_value = np.mean(data)
    return round(mean_value, precision)

def parse_stem_and_leaf(data: Tuple[List[int], List[List[int]]]) -> List[float]:

    stems, leaves = data
    full_numbers = []
    for stem, leaf_set in zip(stems, leaves):
        for leaf in leaf_set:
            full_numbers.append(stem * 10 + leaf)
    return full_numbers

