def calculate_median(data):

    import numpy as np

    if not data:
        raise ValueError("The data list is empty. Median cannot be calculated.")

    data_sorted = sorted(data)
    n = len(data_sorted)
    mid_index = n // 2

    if n % 2 == 1:
        # If the number of data points is odd, return the middle number
        return data_sorted[mid_index]
    else:
        # If the number of data points is even, return the average of the middle two numbers
        return (data_sorted[mid_index - 1] + data_sorted[mid_index]) / 2.0
