def calculate_cosine(adjacent, hypotenuse):

    if hypotenuse <= 0:
        raise ValueError("Hypotenuse should be greater than zero.")
    return adjacent / hypotenuse

def cosine_identity(angle_degrees, identity_type='double'):

    x_rad = np.radians(angle_degrees)
    if identity_type == 'double':
        return np.cos(2 * x_rad)
    elif identity_type == 'half':
        return np.cos(x_rad / 2)
    else:
        raise ValueError("Unsupported identity type specified. Use 'double' or 'half'.")

def cosine_rule(a, b, c=None, angle_C=None):

    if c and angle_C is None:
        # Calculate the angle C using the cosine rule
        angle_C_rad = math.acos((a**2 + b**2 - c**2) / (2 * a * b))
        angle_C_deg = math.degrees(angle_C_rad)
        return angle_C_deg
    elif angle_C:
        # Calculate side c using the cosine rule
        angle_C_rad = math.radians(angle_C)
        return math.sqrt(a**2 + b**2 - 2 * a * b * math.cos(angle_C_rad))
    else:
        raise ValueError("Either side c or angle C must be provided")

def cosine_addition(angle1, angle2):

    from math import cos, sin
    return cos(angle1) * cos(angle2) - sin(angle1) * sin(angle2)

