def trig_calculator(func_name, angle, degrees=False):

    if degrees:
        angle = np.deg2rad(angle)
    trig_functions = {
        'sin': np.sin,
        'cos': np.cos,
        'tan': np.tan,
        'csc': lambda x: 1 / np.sin(x),
        'sec': lambda x: 1 / np.cos(x),
        'cot': lambda x: 1 / np.tan(x)
    }
    if func_name not in trig_functions:
        raise ValueError("Invalid trigonometric function specified.")
    return trig_functions[func_name](angle)

def trig_identity_sinusoidal_properties(angle_radians, identity="pythagorean"):

    if identity == "pythagorean":
        return sin(angle_radians)**2 + cos(angle_radians)**2

def co_function_transformations(angle_radians, original='sin'):

