def sine(angle: float, radians: bool = True) -> float:

    if not radians:
        angle = math.radians(angle)
    return math.sin(angle)

def calculate_sine_cosine(opposite, adjacent, hypotenuse):

    if opposite is None or adjacent is None or hypotenuse is None:
        raise ValueError("All sides of the triangle must be provided.")
    if opposite < 0 or adjacent < 0 or hypotenuse < 0:
        raise ValueError("Side lengths cannot be negative.")
    if opposite**2 + adjacent**2 < hypotenuse**2:
        raise ValueError("Invalid triangle.")
    sine = opposite / hypotenuse
    cosine = adjacent / hypotenuse
    return {"sine": sine, "cosine": cosine}

def evaluate_sin_cos(angle, in_degrees=True):

    if in_degrees:
        angle = math.radians(angle)

    sin_val = math.sin(angle)
    cos_val = math.cos(angle)

    return sin_val, cos_val

def calculate_side_given_angle(angle, side, function, angle_unit="radians"):
    if angle_unit == "degrees":
        angle = math.radians(angle)

    if function == "sine":
        return side / math.sin(angle)
    elif function == "cosine":
        return side / math.cos(angle)
    elif function == "tangent":
        return side / math.tan(angle)
    else:
        raise ValueError("Invalid function parameter. Must be 'sine', 'cosine', or 'tangent'.")

def calculate_angle_given_ratio(ratio, function):

    if function == "sine":
        if -1 <= ratio <= 1:
            return math.asin(ratio)
        else:
            raise ValueError("Invalid ratio. Ratio must be between -1 and 1.")
    elif function == "cosine":
        if -1 <= ratio <= 1:
            return math.acos(ratio)
        else:
            raise ValueError("Invalid ratio. Ratio must be between -1 and 1.")
    else:
        raise ValueError("Invalid function. Function must be 'sine' or 'cosine'.")

def calculate_sine_period(b: float) -> float:

    period = 2 * math.pi / b
    return period

