def sine_of_angle(angle, unit='radians'):

    if unit == 'degrees':
        angle = np.deg2rad(angle)
    return np.sin(angle)

def arcsine_of_value(value):

    if not -1 <= value <= 1:
        raise ValueError("Input value must be within the range -1 to 1.")
    return math.asin(value)

def unit_circle_coordinates(angles, unit='degrees'):

    if not isinstance(angles, list):
        angles = [angles]

    if unit == 'degrees':
        angles = np.radians(angles)

    coordinates = []
    for angle in angles:
        x = np.cos(angle)
        y = np.sin(angle)
        coordinates.append((x, y))

    if len(coordinates) == 1:
        return coordinates[0]
    else:
        return coordinates

def find_values_of_x(sine_value):

    x_values = []
    for angle in range(0, 360):
        if np.sin(np.radians(angle)) == sine_value:
            x_values.append(angle)
    return x_values
